//Access XML request through Groovy
//XML Parsing
//Populate required nodes in XML request

//before parsing import class 
import com.eviware.soapui.support.XmlHolder

def xmlReq = testRunner.testCase.testSteps["Multiply"].getPropertyValue("Request")
def a = testRunner.testCase.getPropertyValue("a")
def b = testRunner.testCase.getPropertyValue("b")

def samplexmlreq = new XmlHolder(xmlReq)

samplexmlreq.setNodeValue("//tem:Multiply/tem:intA",a)
samplexmlreq.setNodeValue("//tem:Multiply/tem:intB",b)
def finalXml =  samplexmlreq.getXml()

def projectpath = testRunner.testCase.testSuite.project

projectpath.testSuites['RegressionTest12'].testCases['Add TestCase'].setSetupScript('log.info "Symphony"')
projectpath.testSuites['RegressionTest12'].testCases['Add TestCase'].setTearDownScript('log.info "Luminous"')

log.info(" >> "+testRunner.testCase.getSetupScript());
log.info(" >> "+testRunner.testCase.getTearDownScript());

